import React, { Component } from "react";

class createExerciseList extends Component {
    constructor(porps) {
      super(props)
    this.state = {
      username: "",
      description: "",
      duration: "",
      date: new Date(),
    };
  }

  onUsernameChange = (e) => {
    this.setState({
      username: e.target.value,
    });
  };

  onDescriptionChange = (e) => {
    this.setState({
      description: e.target.value,
    });
  };

  onDurationChange = (e) => {
    this.setState({
      duration: e.target.value,
    });
  };

  onDateChange = (date) => {
    this.setState({
      date: date,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const excercise = {
      username: this.state.username,
      description: this.state.description,
      duration: this.state.duration,
      date: this.state.date,
    };

    console.log(excercise);
    window.location = "/";
  };

  render() {
    return(
    <form onSubmit={this.onSubmit}>
      <h3>Create Exercise</h3>
      <label>Username</label>
      <select
        value={this.state.username}
        onChange={this.onUsernameChange()}></select>
      <label>Description</label>
      <input
        value={this.state.description}
        onChange={this.onDescriptionChange}></input>
      <label>Duration</label>
      <input
        value={this.state.duration}
        onChange={this.onDurationChange}></input>
      <label>Date</label>
      <input value={this.state.date} onChange={this.onDateChange}></input>
          </form>
    )
  }
}
export default createExerciseList;
