import React, { Component } from "react";
import axios from "axios";

class exerciseList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      exercise: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/exercise")
      .then((res) => {
        this.setState({
          exercise: res.data,
        });
      })
      .catch((err) => console.log(err));
  }

  deleteExercise = (id) => {
    axios.delete("http://localhost:5000/exercise" + id).then((res) => {
      console.log(res.data);
      this.setState({
        exercise: this.state.exercise.filter((el) => {
          el._id !== id;
        }),
      });
    });
  };

  render() {
      return
      <div>
        <div>Exercise List</div>;<h3>Exercise Logged</h3>
        <table className='table'>
          <thead>
            <tr>
              <th>Username</th>
              <th>Description</th>
              <th>Duration</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>{this.stateexerciseList()}</tbody>
        </table>
      </div>;
  }
}

export default exerciseList;
