import React, { Component } from "react";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './App.css';

class createExerciseList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      description: "",
      duration: "",
      date: new Date(),
      users: [],
    };
  }

  componentDidMount() {
    this.setState({
      users: ["testUser"],
      username: "testUser",
    });
  }

  onUsernameChange = (e) => {
    this.setState({
      username: e.target.value,
    });
  };

  onDescriptionChange = (e) => {
    this.setState({
      description: e.target.value,
    });
  };

  onDurationChange = (e) => {
    this.setState({
      duration: e.target.value,
    });
  };

  onDateChange = (date) => {
    this.setState({
      date: date,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const excercise = {
      username: this.state.username,
      description: this.state.description,
      duration: this.state.duration,
      date: this.state.date,
    };

    console.log(excercise);
    // window.location = "/";
  };

  render() {
    return (
      <form onSubmit={this.onSubmit} className="formOuter">
        <h3>Create New Exercise</h3>
        <div className="username">
          <label>Username</label>
          <select
            ref='userInput'
            required
            value={this.state.username}
            onChange={this.onUsernameChange}>
            {this.state.users.map((user) => {
              return (
                <option key={user} value={user}>
                  {user}
                </option>
              );
            })}
          </select>
            </div>
            
        <div className="description">
          <label>Description</label>
          <input
            value={this.state.description}
            onChange={this.onDescriptionChange}></input>
        </div>

        <div>
          <label>Duration</label>
          <input
            value={this.state.duration}
            onChange={this.onDurationChange}></input>
        </div>
        <div>
          <label>Date</label>
          <DatePicker value={this.state.date} onChange={this.onDateChange} />
        </div>
        <div>
          <input type='submit' value='Create Exercise' />
        </div>
      </form>
    );
  }
}
export default createExerciseList;
